				int pos = _RdButtons.IndexOf((RdButton)sender);
				string msg = string.Format("ERROR modificando pertenencia a grupo Rtx de frecuencia RD [Pos={0}] [Id={1}]", pos, id);
				_Logger.Error(msg, ex);
			}
		}

